package assgn1;

public class CompOfThreeNums {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5, b = 12, c = 10;
		if(a > b && a > c)
		{
			System.out.println(a + " is gratest");
		}
		else if(b > a && b > c)
		{
			System.out.println(b + " is gratest");
		}	
		else
		{
			System.out.println(c + " is greatest");
		}
	}

}
