package assgn1;

public class ExploreLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i, num = 5;
		i = 1;
		System.out.println("While Loop");
		while(i<=5)  //While
		{
			System.out.println(i);
			i++;
		}
		System.out.println("For Loop");
		for(i = 11; i <= 15; i++)  //For
		{
			System.out.println(i);
		}
		System.out.println("Do While Loop");
		i = 21;
		do {   //do while
			System.out.println(i);
			i++;
		}while(i <= 25);
		

	}

}
